mapper.select:
select a.id, a.verss, a.isdel, a.createtime, a.createuse, a.updatetime, a.updateuse, a.yhid, a.nd, a.zfs
from wf_rykhfsb a
where a.id = #{id}

mapper.update:
update wf_rykhfsb
verss = #{verss}, isdel = #{isdel}, createtime = #{createtime}, createuse = #{createuse}, updatetime = #{updatetime}, updateuse = #{updateuse}, yhid = #{yhid}, nd = #{nd}, zfs = #{zfs},
where id = #{id}
